const StyleDictionary = require(&quot;style-dictionary&quot;);

StyleDictionary.registerTransform({
  name: &quot;variable-contract/name&quot;,
  type: &quot;name&quot;,
  transform: (token) =&gt; {
    // Convert dot-separated path to CSS variable name
    return token.path.join(&quot;-&quot;).replace(/\$/g, &quot;&quot;);
  },
});

